import Header from "../Header/Header";
import axios from "axios";
import "./Contribution.css";

const Contribution = () => {
  const [selectedCampaign, setSelectedCampaign] = useState("");
  const [activeCampaign, setActiveCampaign] = useState("");

  const handleCampaignChange = (event) => {
    setSelectedCampaign(event.target.value);
    axios.post('http://localhost:61316/getdonationdetails',  event.target.value)
      .then(response => {
        // Handle the response if needed
        setActiveCampaign
        console.log('donationDetails:', response.data);



      })
      .catch(error => {
        // Handle any errors
        console.error('Error creating campaign:', error);
      });
  };




  console.log(activeCampaign)

  return (
    <>
      <Header />
      <div className="contribution-container">
        <h1>Contribution</h1>
        <div className="select-container">
          <label htmlFor="campaignDropdown">Select Campaign:</label>
          <select
            id="campaignDropdown"
            className="campaign-dropdown"
            value={selectedCampaign}
            onChange={handleCampaignChange}
          >
            <option value="">-- Select a campaign --</option>
            {activeCampaign.map((campaign) => (
                <option key={campaign.id} value={campaign.id}>
                  {campaign.campaignName}
                </option>
              ))}
          </select>
        </div>
        {/* Rest of the Contribution component */}
      </div>
    </>
  );
};

export default Contribution;
