import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "../Header/Header";




const ActiveCampaign = () => {
    const [campaigns, setCampaigns] = useState([]);
    useEffect(() => {
        // Make the GET request using axios
        axios
          .get("http://localhost:60181/activecampaign")
          .then(response => {
            // Handle the response data
            setCampaigns(response.data);
          })
          .catch(error => {
            // Handle any errors
            console.error("Error retrieving active campaigns:", error);
          });
      }, []);

      console.log("active campaigns are",campaigns)

    return(
        <>
         <Header/>        


         <h1>Active Campaign</h1>
      <div className="campaign-cards">
        {campaigns.map(campaign => (
          <div className="campaign-card" key={campaign.id}>
            <h3 className="highlight">Campaign Name: {campaign.campaignName}</h3>
            <p>Description: {campaign.description}</p>
            <p>City: {campaign.city}</p>
            <p>
              Start Date: <span className="highlight">{campaign.startDate}</span>
            </p>
            <p>
              End Date: <span className="highlight">{campaign.endDate}</span>
            </p>
          </div>
        ))}
      </div>
        </>
    )
}

export default ActiveCampaign
