import React, { useState, useEffect } from "react";
import Header from "../Header/Header";
import axios from "axios";
import "./Contribution.css";

const Contribution = () => {
  const [selectedCampaign, setSelectedCampaign] = useState("");
  const [activeCampaign, setActiveCampaign] = useState([]);
  const [donationDetail, setDonationDetail] = useState([]);
  const [totalDonation, setTotalDonation] = useState(0);

  const handleCampaignChange = (event) => {
    const campaignName = event.target.value;
    console.log("handleCampaignChange", campaignName);
    setSelectedCampaign(campaignName);
    axios
      .post("http://localhost:50575/getdonationdetails", { campaignName })
      .then((response) => {
        // Handle the response if needed
        console.log("donationDetails:", response.data);
        setDonationDetail(response.data);
        calculateTotalDonation(response.data);
      })
      .catch((error) => {
        // Handle any errors
        console.error("Error retrieving donation details:", error);
      });
  };

  const calculateTotalDonation = (donationDetails) => {
    const total = donationDetails.reduce(
      (sum, donation) => sum + donation.donationAmount,
      0
    );
    setTotalDonation(total);
  };

  useEffect(() => {
    // Make the GET request using axios
    axios
      .get("http://localhost:50575/activecampaign")
      .then((response) => {
        // Handle the response data
        setActiveCampaign(response.data);
      })
      .catch((error) => {
        // Handle any errors
        console.error("Error retrieving active campaigns:", error);
      });
  }, []);

  console.log(activeCampaign);

  return (
    <>
      <Header />
      <div className="contribution-container">
        <h1>Contribution</h1>
        <div className="select-container">
          <label htmlFor="campaignDropdown">Select Campaign:</label>
          <select
            id="campaignDropdown"
            className="campaign-dropdown"
            value={selectedCampaign}
            onChange={handleCampaignChange}
          >
            <option value="">-- Select a campaign --</option>
            {activeCampaign.map((campaign) => (
              <option key={campaign.id} value={campaign.id}>
                {campaign.campaignName}
              </option>
            ))}
          </select>
        </div>
        <table className="donation-table">
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Phone</th>
              <th>Email</th>
              <th>Donation Amount</th>
            </tr>
          </thead>
          <tbody>
            {donationDetail.map((donation) => (
              <tr key={donation.id}>
                <td>{donation.firstName}</td>
                <td>{donation.lastName}</td>
                <td>{donation.phone}</td>
                <td>{donation.email}</td>
                <td>{donation.donationAmount}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan="4" className="total-label">
                Total:
              </td>
              <td className="total-amount">{totalDonation}</td>
            </tr>
          </tfoot>
        </table>
      </div>
    </>
  );
};

export default Contribution;
