import Header from "../Header/Header";
import "./Contribution.css";

const Contribution = () => {
  const [selectedCampaign, setSelectedCampaign] = useState("");

  const handleCampaignChange = (event) => {
    setSelectedCampaign(event.target.value);
  };


  useEffect(() => {
    // Make the GET request using axios
    axios
      .get("http://localhost:51305/activecampaign")
      .then(response => {
        // Handle the response data
        setCampaigns(response.data);
      })
      .catch(error => {
        // Handle any errors
        console.error("Error retrieving active campaigns:", error);
      });
  }, []);

  return (
    <>
      <Header />
      <div className="contribution-container">
        <h1>Contribution</h1>
        <div className="select-container">
          <label htmlFor="campaignDropdown">Select Campaign:</label>
          <select
            id="campaignDropdown"
            className="campaign-dropdown"
            value={selectedCampaign}
            onChange={handleCampaignChange}
          >
            <option value="">-- Select a campaign --</option>
            <option value="campaign1">Campaign 1</option>
            <option value="campaign2">Campaign 2</option>
            <option value="campaign3">Campaign 3</option>
          </select>
        </div>
        {/* Rest of the Contribution component */}
      </div>
    </>
  );
};

export default Contribution;
