import React, { useState } from 'react';
import Header from '../Header/Header';
import './Donate.css';
import Popup from './Popup';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


const Donate = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [amount, setAmount] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [donationResult, setDonationResult] = useState(0);

  const navigate = useNavigate();


  // ...

  const handleDonate = (e) => {
    e.preventDefault();

    // Create an object with the form data
    const formData = {
      firstName,
      lastName,
      email,
      phone,
      amount
    };

    // Make the POST request using axios
    axios.post('http://localhost:53357/donationamount', formData)
      .then(response => {
        // Handle the response if needed
        console.log('Donation amount posted successfully:', response.data);
        const donationAmount = parseFloat(amount);
        const donationResult = donationAmount * 0.025;
        setDonationResult(donationResult);
        setShowPopup(true);
      })
      .catch(error => {
        // Handle any errors
        console.error('Error posting donation amount:', error);
      });
  };


  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <>
      <Header />
      <div className="form-container">
        <h2>Donate</h2>
        <form className="donate-form" onSubmit={handleDonate}>
          <div className="form-group">
            <label htmlFor="firstName">First Name:</label>
            <input
              type="text"
              id="firstName"
              name="firstName"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="lastName">Last Name:</label>
            <input
              type="text"
              id="lastName"
              name="lastName"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="phone">Phone:</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="amount">Donation Amount:</label>
            <input
              type="number"
              id="amount"
              name="amount"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <button type="submit">Donate</button>
          </div>
        </form>
      </div>
      {showPopup && (
        <Popup amount={donationResult} onClose={handleClosePopup} />
      )}
    </>
  );
};

export default Donate;
